import React, { useEffect, useState } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import { useForm, Controller } from 'react-hook-form';
import InputMask from 'react-input-mask';
import { URLsocket, socket } from "./Componentes/Config";
import { AlertForm, maskLoginSympla, MostrarIngresso, hexToRgbA } from "./Componentes/Funcoes";
import CabecalhoPagina from "./Componentes/Cabecalho";
import Carregando from "./Componentes/Carregando";

import {
  Coluna,
  BackgroundImg,
  CampoNome,
  CampoEmail,
  FormCadastrar,
  BotaoCadastrar,
  DataEvento,
  LogoLogin,
  Hashtag
} from './Estilos'

import BackgroundPagina from "./images/bg_login.jpg"
import LogoNivea from "./images/logo_nivea.png"
import ImgDataEvento from "./images/data_evento.png"
import ImgDataEvento2 from "./images/data_evento_2.png"

function Login() {

  const history = useHistory();
  const [ Loading, setLoading ] = useState(true);
  const [ Loading2, setLoading2 ] = useState(false);
  const { register, handleSubmit, errors, control, setValue } = useForm();

  const [ NomeEvento, setNomeEvento ] = useState("NIVEA - #OToqueQueTransforma");
  const [ IpClient, SetIpClient ] = useState(localStorage.getItem("IpClient"));
  const [ DesabilitaBotao, setDesabilitaBotao ] = useState(false);
  const [ TipoEvento, setTipoEvento ] = useState();

  useEffect(() => {
    fetch(`https://webaovivo.com.br/PegaIP/`).then(res => res.json()).then(
      json => (
        SetIpClient(json.IPv4),
        localStorage.setItem("IpClient", json.IPv4)
      )
    );
    PegaDadosEvento();
  }, []);



  async function PegaDadosEvento() {

    const response = await fetch(URLsocket + '/PegaDadosEvento/608c0eecd23f5fd6d001f77e', { method: 'GET', headers: {'Content-Type': 'application/json'} });
    const jsonResult = await response.json();

    if(jsonResult !== null) {
      const val = jsonResult[0];

      setTipoEvento(val.c_tipo);
      setLoading(false);

    } else {

      console.log('PegaDadosEvento Falhou');

    }

  }














  async function VerificaOutros(data) {
    socket.emit('VerificaOutros', data);
    console.log(data);
  }





  const onSubmit = async (data, e) => {  

    setLoading2(true);




    if(data.c_email == "" || data.c_email == undefined){
      //console.log('passou');
      AlertForm('O campo E-MAIL é de preenchimento obrigatório!');
      setLoading2(false);
     e.preventDefault();
   }


    else if (/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(data.c_email)) { 



    if(data.c_email !== "" && data.c_email !== undefined){

      setLoading2(true);
      var Email = data.c_email.toLowerCase();

      const response = await fetch(URLsocket + '/LoginLive', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
          c_email: Email.replace(' ', ''),
          c_ip: IpClient
        }),
      });

      const jsonResult = await response.json();
      // console.log(jsonResult);

      //if(jsonResult !== null){
      if(jsonResult.auth == true){

          if(TipoEvento == jsonResult.c_tipo || jsonResult.c_tipo == 2) {

          // AlertForm("Acesso LIBERADO: " + TipoEvento + " - " + jsonResult.c_tipo);

          localStorage.setItem("c_nome", jsonResult.c_nome);
          localStorage.setItem("c_email", jsonResult.c_email);
          localStorage.setItem("c_id", jsonResult.c_id_user);
          localStorage.setItem("LogadoToken", jsonResult.c_token);

          let ListaConfig = JSON.stringify({
            c_email: jsonResult.c_email,
            c_token: jsonResult.c_token            
          });
          VerificaOutros(ListaConfig);

          history.push({
            pathname: 'live',
            state: {}
          });

          setLoading2(false);

          } else {

            AlertForm("Seu e-mail não está cadastrado para este evento.");
            setLoading2(false);

          }

      } else if (jsonResult.auth == "bloqueado") {

        AlertForm('E-mail bloqueado!');
        setLoading2(false);

      } else {

          // console.log('nao encontrado');

          AlertForm('E-mail não cadastrado!');
          setLoading2(false);

          /*
          history.push({
            pathname: 'naoencontrado',
            state: {
              c_nome: jsonResult.c_nome,
              c_email: jsonResult.c_email,
              c_id_user: jsonResult.c_id_user
            }
          });
          */



      }

    }





  } else {


    AlertForm('O campo E-MAIL está com o formato incorreto!');
    setLoading2(false);
    e.preventDefault();


  }

  };








  return (
    <>
        <CabecalhoPagina NomeEvento={NomeEvento} />

        <BackgroundImg Background={BackgroundPagina} BackgroundMob={BackgroundPagina} bgcolor={'#FFFFFF'}>

            {Loading2 === true ? <Carregando /> : null }
            {Loading === true ? <Carregando /> : null }

            <FormCadastrar onSubmit={handleSubmit(onSubmit)}>


              <Coluna>

              <LogoLogin src={LogoNivea} MaxWidth={220} />
              <Hashtag>#OToqueQueTransforma {} </Hashtag>

              { Loading === true ? null : <DataEvento src={TipoEvento == 0 ? ImgDataEvento : ImgDataEvento2} MaxWidth={TipoEvento == 0 ? 352 : 395} /> }

              <CampoEmail>
                <Controller
                  control={control}
                  ref={register}
                  id="c_email"
                  name="c_email"
                  type="email"
                  placeholder={'E-mail'}
                  className="CampoForm CampoFormLogin"
                  as={InputMask}
                  mask=""
                />
              </CampoEmail>

              {
                DesabilitaBotao === true ?
                <BotaoCadastrar disabled style={{opacity: 0.5}}>Entrar</BotaoCadastrar>
                :
                <BotaoCadastrar type="submit">Entrar</BotaoCadastrar>
              }

              </Coluna>

            </FormCadastrar>





        </BackgroundImg>

    </>
  );
}

export default Login;
